import {CellManager as StandaloneCellManager} from "./standalone";

import Jupyter from "base/js/namespace";

export class CellManager extends StandaloneCellManager {
  getCells(){
    return Jupyter.notebook.get_cells().reduce((memo, cell)=> {
      if(cell.metadata.nbpresent){
        memo[cell.metadata.nbpresent.id] = cell;
      }
      return memo;
    }, {});
  }
}

import {d3, html2canvas} from "nbpresent-deps";

// boo nasty hack
window.html2canvas = html2canvas;

import {PARTS, PART_SELECT} from "../parts";

let _thumbs = {};

export class CellManager {
  getCells(){
    let cells = {};

    d3.selectAll(".cell").each(function(){
      let el = d3.select(this),
        id = el.attr("data-nbpresent-id");

      if(id){
        cells[id] = {
          element: [el.node()]
        }
      }
    });
    return cells;
  }

  getPart(content){
    let cell = this.getCells()[content.cell];

    if(!cell){
      return null;
    }

    let $el = d3.select(cell.element[0]),
      part = $el.select(PART_SELECT[content.part]);

    return part;
  }

  thumbnail(content){
    let key = `${content.cell}-${content.part}`
    if(!_thumbs[key]){
      let el = this.getPart(content);
      el = el ? el.node() : null;
      if(!el){
        return Promise.reject(content);
      }
      _thumbs[key] = html2canvas(el)
        .then((canvas) => {
          return {
            canvas,
            uri: canvas.toDataURL("image/png"),
            width: canvas.width,
            height: canvas.height
          };
        }, (err)=>{
          console.log(err)
        });
    }

    return _thumbs[key];
  }
}

