// https://github.com/lucasb-eyer/d3-boundingbox

// ES6 "translation" by (c) 2015 Nick Bollweg

import {d3} from "nbpresent-deps";

export function bbox() {
    // All those are initialized to default further down using the setters.
    var xextent = null
    var yextent = null
    var handlesize = null
    var dirs = null
    var curs = null
    var cbs = {
        dragstart: null,
        dragmove: null,
        dragend: null,
        resizestart: null,
        resizemove: null,
        resizeend: null
    }

    function my(selection) {
        var drag = d3.behavior.drag()
            .origin(function(d, i) { return {x: this.getAttribute("x"), y: this.getAttribute("y")}; })
            .on("drag.lbbbox", dragmove)
            .on("dragstart.lbbbox", dragstart)
            .on("dragend.lbbbox", dragend)
        selection.call(drag)
        selection.on("mousemove.lbbbox", move)
        selection.on("mouseleave.lbbbox", leave)

        return selection
    }

    function clamp(x, extent) { return Math.max(extent[0], Math.min(x, extent[1])); }
    function inside(x, extent) { return extent[0] < x && x < extent[1]; }

    // Will return w, nw, n, ne, e, se, s, sw for the eight borders,
    // M for inside, or "" when current location not in `dirs`.
    function whichborder(xy, elem) {
        var border = ""
        var x = +elem.getAttribute("x")
        var y = +elem.getAttribute("y")
        var w = +elem.getAttribute("width")
        var h = +elem.getAttribute("height")

             if(xy[1] < y + handlesize.n) border += 'n'
        else if(xy[1] > y + h - handlesize.s) border += 's'

             if(xy[0] < x + handlesize.w) border += 'w'
        else if(xy[0] > x + w - handlesize.e) border += 'e'


        if(border == "" && (dirs.indexOf("x") > -1 || dirs.indexOf("y") > -1))
            border = "M"
        else if(dirs.indexOf(border) == -1)
            border = ""

        return border
    }

    function move(d, i) {
        // Don't do anything if we're currently dragging.
        // Otherwise, the cursor might jump horribly!
        // Also don't do anything if no cursors.
        if(this.__resize_action__ !== undefined || !curs)
            return

        var b = whichborder(d3.mouse(this), this)

        var x = dirs.indexOf("x")
        var y = dirs.indexOf("y")
        // Bwahahahaha this works even when one is at index 0.
        if(b == "M" && 1/(x*y) < 0)
            document.body.style.cursor = x >= 0 ? curs.x : curs.y
        else
            document.body.style.cursor = curs[b] || null
    }

    function leave(d, i) {
        // Only unset cursor if we're not dragging,
        // otherwise we get horrible cursor-flipping action.
        // Also only unset it if we actually did set it!
        if(this.__resize_action__ === undefined && curs)
            document.body.style.cursor = null
    }

    function dragstart(d, i) {
        this.__resize_action__ = whichborder(d3.mouse(this), this)
        this.__ow__ = +this.getAttribute("width")
        this.__oh__ = +this.getAttribute("height")

        if(this.__resize_action__ == "M") {
            if(cbs.dragstart) cbs.dragstart.call(this, d, i)
        } else if(this.__resize_action__.length) {
            if(cbs.resizestart) cbs.resizestart.call(this, d, i)
        }
    }

    function dragend(d, i) {
        if(this.__resize_action__ == "M") {
            if(cbs.dragend) cbs.dragend.call(this, d, i)
        } else if(this.__resize_action__.length) {
            if(cbs.resizeend) cbs.resizeend.call(this, d, i)
        }

        delete this.__resize_action__
        delete this.__ow__
        delete this.__oh__

        // Still need to unset here, in case the user stop dragging
        // while the mouse isn't on the element anymore (e.g. off-limits).
        if(curs)
            document.body.style.cursor = null
    }

    function dragmove(d, i) {
        if(this.__resize_action__ == "M") {
            if(cbs.dragmove)
                if(false === cbs.dragmove.call(this, d, i))
                    return
        } else if(this.__resize_action__.length) {
            if(cbs.resizemove)
                if(false === cbs.resizemove.call(this, d, i))
                    return
        }

        // Potentially dynamically determine the allowed space.
        var xext = typeof xextent === "function" ? xextent.call(this, d, i) : xextent
        var yext = typeof yextent === "function" ? yextent.call(this, d, i) : yextent

        // Handle moving around first, more easily.
        if(this.__resize_action__ == "M") {
            if(dirs.indexOf("x") > -1 && d3.event.dx != 0)
                // This is so that even moving the mouse super-fast, this still "sticks" to the extent.
                this.setAttribute("x", clamp(clamp(d3.event.x, xext) + this.__ow__, xext) - this.__ow__)
            if(dirs.indexOf("y") > -1 && d3.event.dy != 0)
                this.setAttribute("y", clamp(clamp(d3.event.y, yext) + this.__oh__, yext) - this.__oh__)
        // Now check for all possible resizes.
        } else {
            var x = +this.getAttribute("x")
            var y = +this.getAttribute("y")

            // First, check for vertical resizes,
            if(/^n/.test(this.__resize_action__)) {
                var b = y + +this.getAttribute("height")
                var newy = clamp(clamp(d3.event.y, yext), [-Infinity, b-1])
                this.setAttribute("y", newy)
                this.setAttribute("height", b - newy)
            } else if(/^s/.test(this.__resize_action__)) {
                var b = clamp(d3.event.y + this.__oh__, yext)
                this.setAttribute("height", clamp(b - y, [1, Infinity]))
            }

            // and then for horizontal ones. Note both may happen.
            if(/w$/.test(this.__resize_action__)) {
                var r = x + +this.getAttribute("width")
                var newx = clamp(clamp(d3.event.x, xext), [-Infinity, r-1])
                this.setAttribute("x", newx)
                this.setAttribute("width", r - newx)
            } else if(/e$/.test(this.__resize_action__)) {
                var r = clamp(d3.event.x + this.__ow__, xext)
                this.setAttribute("width", clamp(r - x, [1, Infinity]))
            }
        }
    }

    my.xextent = function(_) {
        if(!arguments.length) return xextent
        xextent = _ !== false ? _ : [-Infinity, +Infinity]
        return my
    }
    my.xextent(false)

    my.yextent = function(_) {
        if(!arguments.length) return yextent
        yextent = _ !== false ? _ : [-Infinity, +Infinity]
        return my
    }
    my.yextent(false)

    my.handlesize = function(_) {
        if(!arguments.length) return handlesize
        handlesize = !+_ ? _ : {'w': _,'n': _,'e': _,'s': _}  // coolface
        return my
    }
    my.handlesize(3)

    my.cursors = function(_) {
        if(!arguments.length) return curs
        curs = _ !== true ? _ : {
            M: "move",
            x: "col-resize",
            y: "row-resize",
            n: "n-resize",
            e: "e-resize",
            s: "s-resize",
            w: "w-resize",
            nw: "nw-resize",
            ne: "ne-resize",
            se: "se-resize",
            sw: "sw-resize"
        }
        return my
    }
    my.cursors(true)

    my.directions = function(_) {
        if(!arguments.length) return dirs
        dirs = _ !== true ? _ : ["n", "e", "s", "w", "nw", "ne", "se", "sw", "x", "y"]
        return my
    }
    my.directions(true)

    my.on = function(name, cb) {
        if(cb === undefined) return cbs[name]
        cbs[name] = cb
        return my
    }

    my.infect = function(selection) {
        selection.call(my)
        return my
    }

    my.disinfect = function(selection) {
        selection.on(".drag", null)
        selection.on(".lbbbox", null)
        return my
    }

    return my
}

import {d3} from "nbpresent-deps";

import {PART} from "./parts";

import {RegionTree} from "./regiontree";
import {CellManager} from "./cells/notebook";

import {bbox} from "./d3.bbox";

class Editor{
  constructor(slide, selectedRegion) {
    this.slide = slide;
    this.selectedRegion = selectedRegion;
    this.regions = this.slide.select("regions");
    this.cellManager = new CellManager();

    // TODO: make these discrete to base unit
    this.x = d3.scale.linear();
    this.y = d3.scale.linear();


    this.initUI();
    this.sidebar = new RegionTree(this.slide, this.selectedRegion);

    this.initBehavior();

    this.update();

    this.slide.on("update", ()=> { this.killed || this.update(); });
  }

  destroy() {
    this.sidebar.destroy();
    this.$ui.transition()
      .style({opacity: 0})
      .remove();
    this.killed = true;
  }

  initBehavior(){
    this.bbox = bbox();
  }

  initUI(){
    this.$ui = d3.select("body")
      .append("div")
      .classed({nbpresent_editor: 1})
      .style({opacity: 0});

    this.$bg = this.$ui.append("div")
      .classed({slide_bg: 1});

    this.$svg = this.$bg.append("svg");

    this.$defs = this.$svg.append("defs");

    this.$ui.transition()
      .style({opacity: 1});
  }

  padding(){
    return 20;
  }

  aspectRatio(){
    // TODO: put in data
    return 16 / 9;
  }

  bbEnd(el, d){
    let $el = d3.select(el),
      scales = {
        x: this.x,
        y: this.y,
        width: this.x,
        height: this.y
      };

    let selected = this.selectedRegion.get();

    if(!selected){
      return;
    }

    this.slide.merge(["regions", selected.region, "attrs"],
      d3.entries(scales)
        .reduce((memo, attr) => {
          memo[attr.key] = attr.value.invert($el.attr(attr.key));
          return memo;
        }, {}));
  }

  hasContent(part){
    return (d) => (d.value.content || {}).part === part;
  }

  update(){
    let that = this,
      uibb = this.$ui.node().getBoundingClientRect(),
      width = uibb.width - (this.sidebar.width() + (2 * this.padding())),
      height = width / this.aspectRatio(),
      regions = d3.entries(this.regions.get()),
      {slide, region} = this.selectedRegion.get() || {},
      selected = this.selectedRegion.get();

    if(height > uibb.height + 2 * this.padding()){
      height = uibb.height - (2 * this.padding());
      width = height * this.aspectRatio();
    }

    this.x.range([0, width]);
    this.y.range([0, height]);
    let {x, y} = this;

    this.$bg.style({
      left: `${(((uibb.width + this.sidebar.width()) - width) / 2)}px`,
      top: `${(uibb.height - height) / 2}px`,
      width: `${width}px`,
      height: `${height}px`
    });

    this.$svg.attr({width, height});

    regions.sort((a, b) => {
      return (selected && selected.region == a.key) ? -1 :
        (selected && selected.region == b.key) ? 1 :
          (a.value.attrs.z || 0) - (b.value.attrs.z || 0)
    });

    let $region = this.$svg.selectAll(".region")
      .data(regions, (d) => d.key)
      .order();

    $region.exit().remove();

    let regionData = (region) => {
      return directions.map((dir) => {
        return {region, dir};
      })
    };

    $region.enter()
      .append("g")
      .classed({"region": 1})
      .call(($region) => {
        $region.append("rect")
          .classed({region_bg: 1})
          .each(function(d){
            that.bbox.infect(d3.select(this))
              .on("dragend", function(d){ that.bbEnd(this, d) })
              .on("resizeend", function(d){ that.bbEnd(this, d) });
          });
      })
      .on("mousedown", (d) => {
        this.selectedRegion.set({slide: this.slide.get("id"), region: d.key});
      });

    $region
      .classed({
        active: (d) => selected && (d.key == selected.region),
        content_source: this.hasContent(PART.source),
        content_outputs: this.hasContent(PART.outputs),
        content_widgets: this.hasContent(PART.widgets)
      })
    .select(".region_bg")
      .transition()
      .attr({
        width: (d) => x(d.value.attrs.width),
        height: (d) => y(d.value.attrs.height),
        x: (d) => x(d.value.attrs.x),
        y: (d) => y(d.value.attrs.y)
      });

    $region.filter(({value}) => !value.content)
      .select(".region_bg")
      .style({fill: null});


    $region.filter(({value}) => value.content)
      .each(function(d){
        let $region = d3.select(this);
        that.cellManager.thumbnail(d.value.content)
          .catch(function(err){
            console.warn("thumbnail error", err);
          })
          .then(function({uri, width, height}){
            let id = `${d.value.content.part}-${d.value.content.cell}`,
              bg = that.$defs.selectAll(`#${id}`).data([id]);

            bg.enter().append("pattern")
              .attr({
                patternUnits: "userSpaceOnUse",
                id
              })
            .append("image")
              .attr({
                x: 0,
                y: 0
              });

            bg.attr({width, height})
              .select("image")
              .attr({
                "xlink:href": uri,
                width,
                height
              });

            $region.select(".region_bg")
              .style({
                fill: `url(#${id})`
              });
          });
      });
  }
}

export {Editor};

import {d3} from "nbpresent-deps";

let KEY = "manual";

export class ManualLayout {

  constructor(tree, slide, container){
    this.tree = tree;
    this.slide = slide;
    this.container = container;
  }

  static clsKey(){
    return KEY;
  }

  key(){
    return KEY;
  }

  init(){
    let {clientWidth, clientHeight} = this.container;

    this.x = d3.scale.linear()
      .range([0, clientWidth]);
    this.y = d3.scale.linear()
      .range([0, clientHeight]);
  }

  update(region, part){
    let {x, y, width, height, z} = region.value.attrs;

    z = z || 0;

    part.style({
      height: `${this.y(height)}px`,
      left: `${this.x(x)}px`,
      top: `${this.y(y)}px`,
      width: `${this.x(width)}px`
    });
  }

  clean(unpresent){
    unpresent.style({
      left: null,
      top: null,
      width: null,
      height: null
    });
  }
}

import {d3} from "nbpresent-deps";

import {ManualLayout} from "./manual";

let KEY = "treemap";

export class TreemapLayout extends ManualLayout {
  static clsKey(){
    return KEY;
  }

  key(){
    return KEY;
  }

  init(){
    super.init();

    let that = this;

    this.treemap = d3.layout.treemap()
      .size([100, 100])
      .sticky(true)
      .value((d) => {
        return d._value.attrs["treemap:weight"] || 1;
      });

    let regions = d3.entries(this.slide.value.regions)
      .map((d)=> {
        d._value = d.value;
        return d;
      })

    // initialize missing values
    regions
      .filter((d) => d.value.attrs["treemap:weight"] == null)
      .map((d) => {
        that.tree.merge(["slides", that.slide.key, "regions", d.key, "attrs"], {
          "treemap:weight": d.value["treemap:weight"] || 1
        });
      });

    this.treemap({
      children: regions
    });

    regions.map((d) => {
      let path = ["slides", that.slide.key, "regions", d.key, "attrs"];
      let old = that.tree.get(path),
        newValues = {
          x: d.x / 100,
          y: d.y / 100,
          width: d.dx / 100,
          height: d.dy / 100
        },
        toSet;

      for(var key in newValues){
        if(old[key] !== newValues[key]){
          (toSet ? toSet : toSet = {})[key] = newValues[key];
        }
      }

      if(toSet){
        that.tree.merge(path, toSet);
      }
    });
  }
}

import {d3} from "nbpresent-deps";

import {CellManager} from "./cells/notebook";
import {PART} from "./parts";

class MiniSlide {
  constructor(selectedRegion) {
    this.selectedRegion = selectedRegion;
    this.cellManager = new CellManager();

    this._regions = (d, i) => d.value.regions;

    this.update = this.update.bind(this);
    this.clicked = this.clicked.bind(this);
  }

  regions(_){
    return arguments.length ?
      [this._regions = d3.functor(_), this][1] :
      this._regions;
  }

  hasContent(part){
    return (d) => (d.region.value.content || {}).part === part;
  }

  clicked(d){
    // if called outside the d3 context...
    d = d || d3.select(this).datum();

    if(!this.selectedRegion){
      return;
    }

    let {slide, region} = this.selectedRegion.get() || {};

    if(slide === d.slide.key && region === d.region.key){
      return this.selectedRegion.set(null);
    }

    this.selectedRegion.set({
      slide: d.slide.key,
      region: d.region.key
    });
  }

  update($slide) {
    let that = this;

    $slide.classed({mini: 1});

    let $region = $slide
      .selectAll(".region")
      .data((d) => d3.entries(this._regions(d)).map((region) => {
        return {slide: d, region};
      }));

    $region.enter()
      .append("div")
      .classed({region: 1})
      .on("click", this.clicked);

    $region.exit()
      .remove();

    let {slide, region} = (
      this.selectedRegion && this.selectedRegion.get()
    ) || {};

    $region
      .classed({
        active: (d) => {
          return d.slide.key === slide && d.region.key === region
        },
        content_source: this.hasContent(PART.source),
        content_outputs: this.hasContent(PART.outputs),
        content_widgets: this.hasContent(PART.widgets)
      })
      // TODO: scale
      .style({
        width: (d) => `${d.region.value.attrs.width * 160}px`,
        height: (d) => `${d.region.value.attrs.height * 90}px`,
        left: (d) => `${d.region.value.attrs.x * 160}px`,
        top: (d) => `${d.region.value.attrs.y * 90}px`
      });

    // TODO: this needs to be much better/faster
    $region
      .filter((d) => d.region.value.content)
      .filter(function(d){
        let el = d3.select(this);
        return el.classed("active") || el.style("background-image") === "none";
      })
      .each(function(d){
        var $region = d3.select(this);
        that.cellManager.thumbnail(d.region.value.content)
          .catch(function(err){
            $region
              .style({
                "background": "#333",
                "background-image": null
              });
          })
          .then(function({uri, width, height}){
            $region
              .style({
                background: null,
                "background-image": `url("${uri}")`
              });
          });
      });
  }
}

export {MiniSlide};

import {d3} from "nbpresent-deps";

export class Mode {
  constructor(root){
    this.root = root;
    this.initStylesheet();
    this.init();
  }

  initStylesheet(){
    let css = d3.select("head")
      .selectAll("link#nbpresent-css")
      .data([1]);

    if(css.node()){
      console.warn("nbpresent extension already loaded!");
      return;
    }

    css.enter()
      .append("link")
      .attr({id: "nbpresent-css"})
      .attr({
        rel: "stylesheet",
        href: `${this.root}/nbpresent.min.css`
      });
  }
}

import $ from "jquery";

import Jupyter from "base/js/namespace";
import {CellToolbar} from "notebook/js/celltoolbar";

import {d3} from "nbpresent-deps";

import {NotebookPresenter} from "../presenter/notebook";
import {Sorter} from "../sorter";
import {PART} from "../parts";
import {NbpresentTour} from "../tour";
import {Tree} from "../tree";

import {Mode as BaseMode} from "./base";

export class Mode extends BaseMode {
  init() {
    let tree = new Tree({
      slides: this.metadata().slides,
      root: this.root
    });

    this.tree = tree.tree;

    this.slides = this.tree.select(["slides"]);
    this.slides.on("update", () => this.metadata(true))

    this.tour = new NbpresentTour(this);
    this.tour.init();

    this.presenter = new NotebookPresenter(this.tree, this.tour);
    this.sorter = new Sorter(this.tree, this.tour);

    this.initToolbar();
  }

  metadata(update){
    let md = Jupyter.notebook.metadata;
    if(update){
      md.nbpresent = {
        slides: this.slides.serialize()
      };
    }else{
      return md.nbpresent || {
        slides: {}
      }
    }
  }

  show(){
    this.sorter.show();
    this.tour.start();
    $('#header-container').toggle();
    $('.header-bar').toggle();
  }

  present(){
    this.presenter.presenting.set(true);
  }

  unpresent(){
    this.presenter.presenting.set(false);
  }

  initToolbar() {
    $("#view_menu").append($("<li/>").append($("<a/>")
      .text("Toggle Present")
      .on("click", ()=>{
        this.show();
      })));

    // TODO: make this one button!
    Jupyter.toolbar.add_buttons_group([
      {
        label: "Slide Sorter",
        icon: "fa-th-large",
        callback: () => this.show(),
        id: "nbpresent_sorter_btn"
      },
      {
        label: "Present",
        icon: "fa-youtube-play",
        callback: () => this.present(),
        id: "nbpresent_present_btn"
      }
    ]);
    let that = this;

    var nbpresent_preset = [];

    let add_to_region = (div, cell) => {
      let $div = d3.select(div[0]);

      $div.append("i").attr("class", "fa fa-link");

      $div.append("span").text(" Region ");

      let $btn = $div.selectAll(".btn")
        .data([PART.source, PART.outputs, PART.widgets])
        .enter()
        .append("button")
        .classed({btn: 1, "btn-default": 1, "btn-xs": 1})
        .text((d) => d)
        .on("click", (d) => that.sorter.linkContent(d));
    };

    CellToolbar.register_callback("nbpresent.add_to_region", add_to_region);
    nbpresent_preset.push("nbpresent.add_to_region");

    CellToolbar.register_preset("nbpresent", nbpresent_preset, Jupyter.notebook);

    // update the download chrome
    let dlMenu = d3.select(d3.select("#download_html").node().parentNode);

    dlMenu.insert("li", ":nth-child(4)")
      .append("a")
      .classed({download_nbpresent_html: 1})
      .text("Presentation (.zip)")
      .on("click", () => this.nbconvert("nbpresent"));

    dlMenu.insert("li", ":nth-child(5)")
      .append("a")
      .classed({download_nbpresent_pdf: 1})
      .text("Presentation (.pdf)")
      .on("click", () => this.nbconvert("nbpresent_pdf"));

  }

  nbconvert(key){
    Jupyter.menubar._nbconvert(key, true);
  }
}

import {d3} from "nbpresent-deps";

import {Tree} from "../tree";
import {StandalonePresenter} from "../presenter/standalone";

import {Mode as BaseMode} from "./base";

export class Mode extends BaseMode {
  init() {
    let slides = JSON.parse(d3.select("#nbpresent_tree").text());

    let tree = new Tree({
      slides: slides.slides,
      root: this.root
    });

    this.tree = tree.tree;

    this.presenter = new StandalonePresenter(this.tree);

    this.presenter.presenting.set(true);
  }
}

const PART = {
  source: "source",
  outputs: "outputs",
  widgets: "widgets"
};

const PART_SELECT = {
  source: ".inner_cell",
  outputs: ".output_wrapper",
  widgets: ".widget-area .widget-subarea"
};

export {PART, PART_SELECT};

import {d3} from "nbpresent-deps";

import {SpeakerBase} from "../speaker/base";

import {ManualLayout} from "../layout/manual";
import {TreemapLayout} from "../layout/treemap";

import {PARTS, PART_SELECT} from "../parts";
import {SlabStyle} from "../style/slab";

let PREFIX = [
  "-webkit-",
  "-moz-",
  "-ms-",
  "-o-",
  ""
];

export class Presenter {
  constructor(tree) {
    this.tree = tree;

    this.cellManager = this.makeCellManager();
    this.speaker = this.makeSpeaker(this.tree);

    this.initUI();

    this.presenting = this.tree.select(["presenting"]);
    this.current = this.tree.select(["selectedSlide"]);

    this.tree.on("update", () => this.update());
    this.presenting.on("update", () => this.present());
    this.current.on("update", () => this.update());
  }

  makeCellManager() {
    throw new Error("Not implemented");
  }

  makeSpeaker(tree){
    return new SpeakerBase(tree);
  }

  initUI(){
    this.$ui = d3.select("body")
      .append("div")
      .classed({nbpresent_presenter: 1})
      .style({display: "none"});
  }



  layoutClass(slide){
    return {
      manual: ManualLayout,
      treemap: TreemapLayout
    }[slide.value.layout || "manual"];
  }

  updateLayout(slide){
    let LayoutClass = this.layoutClass(slide);

    if(this.layout &&
      this.layout.key() == LayoutClass.clsKey() &&
      this.layout.slide.key === slide.key
    ){
      this.layout.slide = slide;
    }else{
      this.layout = new LayoutClass(
        this.tree,
        slide,
        document.documentElement
      );
    }
    this.layout.init();
  }

  present() {
    this.update();
  }

  getCells() {
    return this.cellManager.getCells();
  }

  update() {
    let that = this;
    let presenting = this.presenting.get();

    d3.select("body").classed({nbpresent_presenting: presenting});

    this.$ui
      .style({
        "display": presenting ? "block" : "none"
      })
      .style({
        opacity: +presenting,
      });

    let current = this.current.get(),
      slide = this.tree.get(["sortedSlides", (d) => d.key == current]);

    // TODO: handle cleanup
    // transition = this.layout && this.layout.destroy()

    if(!slide){
      return this.current.set(this.tree.get(["sortedSlides", 0, "key"]));
    }

    this.updateLayout(slide);

    if(!presenting){
      return this.clean(true);
    }

    let cells = this.getCells();

    d3.selectAll(this.allPartSelect())
      .classed({nbpresent_unpresent: 1, nbpresent_present: 0});

    d3.entries(slide.value.regions)
      .filter(({value}) => value.content)
      .map((region) => {
        let {content} = region.value,
          cell = cells[content.cell];

        if(!cell){
          return;
        }

        let $el = d3.select(cell.element[0]),
          part = $el.select(PART_SELECT[content.part]);

        part
          .classed({
            nbpresent_unpresent: 0,
            nbpresent_present: 1
          })
          .each(() => that.layout.update(region, part));
        // TODO: now do styles
        d3.entries(region.value.style).map((style) => {
          // TODO: make this extensible

          if(style.value && style.key === "slab"){
            let styler = new SlabStyle();
            styler.update(part);
          }
        })
      });



    this.clean();
  }

  allPartSelect(){
    return d3.entries(PART_SELECT)
      .map(({value}) => `.cell ${value}`)
      .join(", ");
  }

  clean(force){
    let that = this;

    if(force){
      d3.selectAll(this.allPartSelect())
        .classed({nbpresent_unpresent: 1, nbpresent_present: 0});
    }

    d3.selectAll(".nbpresent_unpresent")
      .call(that.layout && this.layout.clean || (() => 0))
      .classed({nbpresent_unpresent: 0, nbpresent_present: 0});
  }
}

