class TemplateLibrary {
  constructor(picked) {
    this.picked = picked;

    this.initUI();

    this.mini = new MiniSlide();

    this.fakeSlide = this.fakeSlide.bind(this);
    this.update = this.update.bind(this);

    this.update();
  }

  destroy() {
    this.$ui.transition()
      .style({opacity: 0})
      .remove();
    this.killed = true;
  }

  fakeSlide(template) {
    let id = uuid.v4();
    return {
      key: id,
      value: {
        id,
        regions: template.regions.reduce((memo, template) => {
          let id = uuid.v4();
          memo[id] = $.extend({}, {id}, {attrs: template});
          return memo;
        }, {})
      }
    }
  }

  initUI(){
    this.$ui = d3.select("body")
      .append("div")
      .classed({nbpresent_template_library: 1});

    this.$ui.append("button")
      .classed({btn: 1, hide_library: 1, "btn-default": 1})
      .on("click", () => this.picked(null))
      .append("i")
      .classed({fa: 1, "fa-remove": 1});

    this.$ui.append("h3")
      .classed({from_template: 1})
      .text("Pick template");

    this.$ui.append("h3")
      .classed({from_slide: 1})
      .text("Reuse slide as template");
  }

  update(){
    let $slide = this.$ui.selectAll(".slide")
      .data(_templates.map(this.fakeSlide));

    $slide.enter()
      .append("div")
      .classed({slide: 1})
      .call(this.mini.update);

    $slide.on("click", this.picked);
  }
}

export {TemplateLibrary};

import {d3} from "nbpresent-deps";

let fn = d3.functor;

class Toolbar {
  constructor(){
    this._btnClass = fn("btn-default");
    this._btnGroupClass = fn("btn-group");
    this._tipOptions = fn({container: "body"});

    this.update = this.update.bind(this);
  }

  btnClass(val){
    return arguments.length ?
      [this._btnClass = fn(val), this][1] :
      this._btnClass;
  }

  tipOptions(val){
    return arguments.length ?
      [this._tipOptions = fn(val), this][1] :
      this._tipOptions;
  }

  btnGroupClass(val){
    return arguments.length ?
      [this._btnGroupClass = fn(val), this][1] :
      this._btnGroupClass;
  }

  update($selection){
    let that = this;
    $selection.classed({"btn-toolbar": 1, nbpresent_toolbar: 1});

    let $group = $selection.selectAll(".btn-toolbar-group")
      .data((d) => d)

    $group.enter()
      .append("div")
      .classed({"btn-toolbar-group": 1})
      .classed(this._btnGroupClass(), 1);

    let $btn = $group.selectAll(".btn")
      .data((d) => d, (d) => d.tip);

    $btn.enter()
      .append("a")
      .attr("class", (d) => {
        return `btn ${this._btnClass(d)}`
      })
      .attr({
        title: (d) => d.tip
      })
      .each(function(d){
        if(!$.fn.tooltip){
          return;
        }
        $(this).tooltip(that._tipOptions(d));
      })
      .append("i");

    $btn
      .on("click", (d) => d.click() )
      .select("i")
      .attr({
        "class": (d) => `fa fa-fw fa-2x fa-${d.icon}`,
      });

    let clean = function(){
      $(this).tooltip("destroy");
    }

    $btn.filter((d) => d.visible && !d.visible())
      .each(clean)
      .remove();

    $btn.exit()
      .each(clean)
      .remove();
    $group.exit().remove();
  }
}

export {Toolbar};

